import React, { useEffect, useState } from "react";
import axios from "axios";
import ligasDisplay from "../assets/data"; // array com { id, nome, foto, ... }
import "../assets/scss/leagues.scss";

const API_URL = process.env.REACT_APP_API_URL;

const Leagues = () => {
  const [ligas, setLigas] = useState([]);

  useEffect(() => {
    const fetchLigas = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/leagues`);
        const ligasApi = response.data;

        // Combina dados da API com dados locais pelo id ou nome
        const ligasComFotos = ligasApi.map((ligaApi) => {
          const ligaLocal = ligasDisplay.find(
            (l) =>
              l.id === ligaApi.id ||
              l.nome.toLowerCase() === ligaApi.nome.toLowerCase()
          );
          return {
            ...ligaApi,
            foto: ligaLocal ? ligaLocal.foto : null,
          };
        });

        setLigas(ligasComFotos);
      } catch (error) {
        console.error("Erro ao buscar as ligas:", error);
      }
    };

    fetchLigas();
  }, []);

  return (
    <div id="Leagues">
      <h1>Mais Buscadas</h1>

      <div id="leagues-display">
        {ligas.length > 0 ? (
          ligas.map((league) => (
            <div key={league.id} className="league">
              <img
                src={league.foto || "url-para-imagem-padrao.png"}
                alt={league.nome}
              />
              <p>{league.nome}</p>
            </div>
          ))
        ) : (
          <p>Carregando ligas...</p>
        )}
      </div>
    </div>
  );
};

export default Leagues;
