import React, { useEffect, useState } from "react";
import axios from "axios";
import ligasDisplay from "../assets/data";
import "../assets/scss/leagues.scss";

const API_URL = process.env.REACT_APP_API_URL;

const Leagues = () => {
  const [ligas, setLigas] = useState([]);

  useEffect(() => {
    const fetchLigas = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/leagues`);
        setLigas(response.data);
      } catch (error) {
        console.error("Erro ao buscar as ligas:", error);
      }
    };

    fetchLigas();
  }, []);

  return (
    <div id="Leagues">
      <h1>Mais Buscadas</h1>

      <div id="leagues-display">
        {ligas.length > 0 ? (
          ligas.map((league) => (
            <div key={league.id} className="league">
              {/* Aqui você pode colocar imagens reais se tiver a URL, senão use ligasDisplay */}
              <img src={league.foto || "default-image-url"} alt={league.nome} />
              <p>{league.nome}</p>
            </div>
          ))
        ) : (
          <p>Carregando ligas...</p>
        )}
      </div>
    </div>
  );
};

export default Leagues;
